    var pos = this.target.position;
    if ( this.target.getPos )
      pos = this.target.getPos();
    // focus a camera ?
    if ( !pos )
      pos = this.target.sceneContainer;
    
    if ( !this.focusLock.x )
      this.position.x = pos.x + ( this.focusOffset.x || 0 );
    if ( !this.focusLock.y )
      this.position.y = pos.y + ( this.focusOffset.y || 0 );
